import React, { useState }  from "react";
import Card from "../UI/Card";
import classes from "./AddUser.module.css"
import Button from "../UI/Button";
const AddUser = (props) =>{

    const [enteruserName , setEnteruserName] = useState('')
    const [enteruserAge , setEnteruserAge] = useState('')
    const AdduserHandler = (event)=>{
        event.preventDefault()

        console.log(enteruserName ,enteruserAge )
        setEnteruserAge(" ")
        setEnteruserName(" ")
    }

    const usernameHandler = (event)=>{
        setEnteruserName(event.target.value)
    }

    const userAgeHandler = (event)=>{
        setEnteruserAge(event.target.value)
    }

    return(
        <Card className={classes.input}>
        <form   onSubmit={AdduserHandler}>
            <label htmlFor="username">Username</label>
            <input id="username" type="text" value={enteruserName} onChange={usernameHandler} placeholder="Enter your name" />
            <label htmlFor="age">Age (Year)</label>
            <input id="age" type="text"value={enteruserAge} onChange={userAgeHandler} placeholder="Enter your name" />
            <Button type="submit">Add User</Button>
        </form>
        </Card>
    )
}

export default AddUser;
